package forSolitaire;//Ching Yupublic class Card  {	// string representation of the suit	private String myRank;	// string representation of the rank	private String mySuit;	// value depends on the game	private int myValue;	// all Cards are constructed face down by default 	// true for face up, false for face down	private boolean myFaceUp = false; 	public Card(String theRank, String theSuit, int theValue) {		myRank= theRank;		mySuit= theSuit;		myValue = theValue;		myFaceUp = false;	}//3 parameter Card constructor	public Card(String theRank, String theSuit) {		myRank= theRank;		mySuit= theSuit;		myValue = 0;//default assignment		myFaceUp = false;	}//2 parameter Card constructor	//returns string representation of all instance variables	public String toString() { 		return "My rank is " + myRank + "\n" 		+ "My suit is " + mySuit + "\n"		+ "My face up value is " + myFaceUp + "\n"		+ "My value is " + myValue + ".\n";	}//toString method	//returns string for display of card	public String displayCard() { 		if (myFaceUp) {			return myRank + " of " + mySuit;		}//if		return "************";	}//displayCard method	// access method, returns suit of the Card	public String getSuit() {		return mySuit;	}//getSuit method	// access method, returns rank of the Card	public String getRank() {		return myRank;	}//getRank method	// access method, returns value of the Card	public int getValue() {		return myValue;	}//getValuemethod	// access method, returns face up  status of the Card	public boolean isFaceUp() {   		return myFaceUp;	}//isFaceUp method	//modifier method, sets myValue	public void setValue(int theValue) {		myValue = theValue;	}//setValue method	//modifier method, sets myFaceUp	public void setFaceUp(boolean theFaceUp) { 		myFaceUp = theFaceUp;	}//setFaceUp method}// Card class